mapper.select:
select id, reply_content, member_id, member_name, post_id, created_datetime, updated_datetime
from view_reply_member
where post_id = #{postId}
order by id desc
limit #{criteria.count} offset #{criteria.offset}

mapper.update:
update tbl_reply
set reply_content = #{replyContent}, updated_datetime = current_timestamp
where id = #{id}
